import React from "react";
import TextField from "@material-ui/core/TextField";
import { makeStyles } from "@material-ui/core/styles";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import Fab from "@material-ui/core/Fab";
import AddIcon from "@material-ui/icons/Add";
import DeleteIcon from "@material-ui/icons/Delete";

const AddRecipe = (props) => {
  const classes = useStyles();

  const stepsInputInfo = () => {
    return (
      <div style={{ width: "100%" }}>
        <TextField
          error={false}
          id="stepsValueId"
          label="Value"
          defaultValue=""
          helperText=""
          variant="outlined"
          style={{ width: "10%" }}
        />
        <FormControl
          variant="outlined"
          style={{ width: "10%", marginTop: "0.5rem" }}
          error={false}
        >
          <InputLabel id="stepsUnitLabel">Unit</InputLabel>
          <Select
            labelId="stepsUnitLabel"
            id="stepsUnitSelect"
            //   value={age}
            //   onChange={handleChange}
            label="Unit"
          >
            <MenuItem value="">
              <em>None</em>
            </MenuItem>
            <MenuItem value={"cl"}>cl</MenuItem>
            <MenuItem value={"mg"}>mg</MenuItem>
            <MenuItem value={"lt"}>litre</MenuItem>
          </Select>
        </FormControl>
        <TextField
          style={{ width: "57%" }}
          error={false}
          id="outlined-error-helper-text"
          label="Step Info"
          defaultValue=""
          helperText=""
          variant="outlined"
        />
        <DeleteIcon color="error" style={{ marginTop: "1.5rem" }} />
      </div>
    );
  };

  return (
    <div>
      Add Recipe
      <form className={classes.root} noValidate autoComplete="off">
        <div>
          <TextField
            error={false}
            id="outlined-error-helper-text"
            label="Titre"
            defaultValue=""
            helperText=""
            variant="outlined"
          />
        </div>
        <div>
          <TextField
            error={false}
            id="outlined-error-helper-text"
            label="Description"
            defaultValue=""
            helperText=""
            variant="outlined"
          />
        </div>
        <div>
          <FormControl
            variant="outlined"
            className={classes.formControl}
            error={false}
          >
            <InputLabel id="demo-simple-select-outlined-label">
              Niveau
            </InputLabel>
            <Select
              labelId="demo-simple-select-outlined-label"
              id="demo-simple-select-outlined"
              //   value={age}
              //   onChange={handleChange}
              label="Age"
            >
              <MenuItem value="">
                <em>None</em>
              </MenuItem>
              <MenuItem value={10}>Ten</MenuItem>
              <MenuItem value={20}>Twenty</MenuItem>
              <MenuItem value={30}>Thirty</MenuItem>
            </Select>
          </FormControl>
        </div>
        <div>
          <TextField
            error={false}
            id="outlined-error-helper-text"
            label="Personnes"
            defaultValue=""
            helperText=""
            variant="outlined"
            type="number"
          />
        </div>
        <div>
          <TextField
            error={false}
            id="outlined-error-helper-text"
            label="TempsPreparation"
            defaultValue=""
            helperText=""
            variant="outlined"
            type="number"
          />
        </div>
        <div>{stepsInputInfo()}</div>
        <div
          style={{
            display: "flex",
            alignItems: "flex-start",
            marginLeft: "9rem",
          }}
        >
          <Fab
            variant="extended"
            color="primary"
            aria-label="add"
            className={classes.margin}
          >
            <AddIcon />
            Add Step
          </Fab>
        </div>
      </form>
    </div>
  );
};

export default AddRecipe;

const useStyles = makeStyles((theme) => ({
  root: {
    "& .MuiTextField-root": {
      margin: theme.spacing(1),
      width: "80%",
    },
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: "80%",
  },
}));
