import axios from "axios";
import React, { useEffect, useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import Grid from "@material-ui/core/Grid";
import TextField from "@material-ui/core/TextField";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import Select from "@material-ui/core/Select";
import FormControl from "@material-ui/core/FormControl";
import CardContent from "@material-ui/core/CardContent";
import Button from "@material-ui/core/Button";
import Fab from "@material-ui/core/Fab";
import Typography from "@material-ui/core/Typography";
import { history } from "../index";
import { convertMinsToHours } from "../helpers/utilities";

const ViewRecipe = () => {
  const classes = useStyles();
  const [recipeList, setRecipeList] = useState([]);
  const [originalList, setOriginalList] = useState([]);

  useEffect(() => {
    getRecipeList();
  }, []);

  const getRecipeList = () => {
    axios
      .get("http://localhost:9000/api/recipes")
      .then((resp) => {
        console.log(resp.data);
        setRecipeList(resp.data);
        setOriginalList(resp.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const onTitleSearch = (event) => {
    if (event.target.value === "") {
      setRecipeList(originalList);
      return;
    }
    const filteredList = originalList.filter((x) =>
      x.titre.toLowerCase().includes(event.target.value.toLowerCase())
    );
    //console.log(filteredList);
    setRecipeList(filteredList);
  };

  const onDifficultyChange = (event) => {
    console.log(event);
    // if (event.target.value === "") {
    //   setRecipeList(originalList);
    //   return;
    // }
    // const filteredList = originalList.filter((x) =>
    //   x.niveau.toLowerCase().includes(event.target.value.toLowerCase())
    // );
    // //console.log(filteredList);
    // setRecipeList(filteredList);
  };

  const onPreperationTimeSearch = (event) => {
    if (event.target.value === "") {
      setRecipeList(originalList);
      return;
    }
    const filteredList = originalList.filter(
      (x) => x.tempsPreparation <= event.target.value
    );
    //console.log(filteredList);
    setRecipeList(filteredList);
  };

  const onClickOfRecipe = (recipeDetails) => {
    history.push("recipe/" + recipeDetails.id);
  };

  const renderCardView = (recipe, index) => {
    return (
      <Card
        className={classes.root}
        key={index.toString()}
        onClick={() => onClickOfRecipe(recipe)}
      >
        <div className={classes.details}>
          <img className={classes.cover} src={recipe.photo} />
          <CardContent className={classes.content}>
            <div>
              <Typography variant="subtitle1" color="textSecondary">
                {recipe.titre}
              </Typography>
              <Typography variant="subtitle2" color="textSecondary">
                {recipe.description}
              </Typography>
              <Typography variant="subtitle2" color="textSecondary">
                <span>Difficulty Level : </span>
                <span>{recipe.niveau}</span>
              </Typography>
              <Typography variant="subtitle2" color="textSecondary">
                <span>Number of People : </span>
                <span>{recipe.personnes}</span>
              </Typography>
              <Typography variant="subtitle2" color="textSecondary">
                <span>Preparation time : </span>
                <span>{convertMinsToHours(recipe.tempsPreparation)}</span>
              </Typography>
            </div>
            <div className={classes.controls}>
              <div className={classes.buttonRoot}>
                <Button variant="contained" color="primary">
                  Modify
                </Button>
                <Button variant="contained" color="secondary">
                  Delete
                </Button>
              </div>
            </div>
          </CardContent>
        </div>
      </Card>
    );
  };

  const renderFilter = () => {
    return (
      <div style={{ flexGrow: 1, margin: "0.5rem" }}>
        <Grid container spacing={3}>
          <Grid item xs={3}>
            <TextField
              label="Title"
              id="outlined-size-small"
              defaultValue=""
              variant="outlined"
              size="small"
              type="search"
              onChange={onTitleSearch}
            />
          </Grid>
          <Grid item xs={3}>
            <FormControl variant="outlined" className={classes.formControl}>
              <InputLabel id="demo-simple-select-label">
                Diffuculty Level
              </InputLabel>
              <Select
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                // value={"padawan"}
                label="Diffuculty Level"
                onChange={onDifficultyChange}
              >
                <MenuItem value={"padawan"}>padawan</MenuItem>
                <MenuItem value={"jedi"}>jedi</MenuItem>
                <MenuItem value={"maitre"}>maitre</MenuItem>
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={3}>
            <TextField
              label="Number of People"
              id="outlined-size-small"
              defaultValue=""
              variant="outlined"
              size="small"
            />
          </Grid>
          <Grid item xs={3}>
            <TextField
              label="Preperation Time"
              id="outlined-size-small"
              defaultValue=""
              variant="outlined"
              size="small"
              type="number"
              onChange={onPreperationTimeSearch}
            />
          </Grid>
        </Grid>
      </div>
    );
  };

  return (
    <div>
      <div style={{ margin: "1rem" }}>
        <Fab
          variant="extended"
          color="primary"
          aria-label="add"
          className={classes.fab}
          onClick={() => history.push("addRecipe")}
        >
          Add Recipe
        </Fab>

        {renderFilter()}
        <div style={{ display: "grid", gridTemplateColumns: "repeat(4, 1fr)" }}>
          {recipeList.map((each, index) => renderCardView(each, index))}
        </div>
      </div>
    </div>
  );
};

export default ViewRecipe;

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    margin: "0.5rem",
    "&:hover": {
      cursor: "pointer",
    },
  },
  buttonRoot: {
    "& > *": {
      margin: theme.spacing(1),
    },
  },
  details: {
    display: "flex",
    flexDirection: "column",
  },
  content: {
    flex: "1 0 auto",
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
  },
  cover: {
    //width: "auto",
    width: "100%",
    height: "50%",
  },
  controls: {
    display: "flex",
    alignItems: "center",
    paddingLeft: theme.spacing(1),
    paddingBottom: theme.spacing(1),
  },
  playIcon: {
    height: 38,
    width: 38,
  },
  formControl: {
    // margin: theme.spacing(1),
    minWidth: 150,
  },

  fab: {
    position: "fixed",
    bottom: "1rem",
    right: "1rem",
  },
}));
